mapper.update:
UPDATE t_clothing SET
  c_cloth_icon = #{clothIcon},c_desc = #{description}, n_createTime = #{createTime}
  WHERE n_id = #{id}

mapper.select:
SELECT n_id as id, c_cloth_icon as clothIcon, c_desc as description, n_createTime as createTime
FROM t_clothing
WHERE n_id = #{id}

SELECT n_id as id, c_cloth_icon as clothIcon, c_desc as description, n_createTime as createTime
FROM t_clothing
limit #{offset}, #{limit}
